package io.tripled.adventofcode.daythree;

import java.util.stream.Stream;

public class Main {

  public static void main(String[] args) {
    BathroomKeyController controller = new BathroomKeyController();
    String input = "\n" +
        "\n" +
        "\n" +
        "\n" +
        "";

    Stream.of(input.split("\n"))
        .map(line -> {
          controller.move(line);
          return controller.getCurrentKey();
        })
        .forEach(System.out::println);
  }
}
